package main

import "fmt"

var (
	input = ``
)

func main() {
	partOne()
	partTwo()
}

func partOne() {
	var marker = ""

	cursor := -1

	for i := 0; i < len(input)-3; i++ {
		marker = input[i:(i + 4)]
		if isMarkerUnique(marker) {
			cursor = i + 4
			break
		}
	}

	fmt.Println(cursor)
}

func partTwo() {
	var marker = ""

	cursor := -1

	for i := 0; i < len(input)-13; i++ {
		marker = input[i:(i + 14)]
		if isMarkerUnique(marker) {
			cursor = i + 14
			break
		}
	}

	fmt.Println(cursor)
}

func isMarkerUnique(marker string) bool {
	for i := 0; i < len(marker)-1; i++ {
		for j := i + 1; j < len(marker); j++ {
			if marker[i] == marker[j] {
				return false
			}
		}
	}

	return true
}
